frappe.ui.form.on('Production Plan', {
	refresh(frm) {
		// your code here
	}
})

frappe.ui.form.on("Material Request Plan Item", {
    custom_adjusted_qty: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];

        if (row.custom_adjusted_qty && row.custom_adjusted_qty > 0) {
            row.required_bom_qty = row.custom_adjusted_qty;
        } else {
            row.required_bom_qty = row.quantity;
        }

        // Refresh the parent child table field (from Production Plan: mr_items)
        frm.refresh_field("mr_items");
    }
});

const DEFAULT_WAREHOUSE = "Factory-WIP - SYN";

frappe.ui.form.on("Production Plan", {
    refresh: function(frm) {
        add_populate_button(frm);
    }
});

function add_populate_button(frm) {
    frm.add_custom_button("Populate MR Items", function() {
        populate_grouped_mr_items(frm).then(() => {
            add_populate_button(frm); // re-add so it doesn't disappear
        });
    });
}

function populate_grouped_mr_items(frm) {
    return new Promise(function(resolve) {
        // Clear table
        frm.clear_table("mr_items");

        if (!frm.doc.po_items || !frm.doc.po_items.length) {
            frm.refresh_field("mr_items");
            return resolve();
        }

        const all_rows = [];
        let promises = [];

        frm.doc.po_items.forEach(function(pp_item) {
            if (!pp_item.bom_no) return;

            const planned_qty = pp_item.planned_qty || 1;

            let p = frappe.call({
                method: "erpnext.manufacturing.doctype.bom.bom.get_bom_items",
                args: { bom: pp_item.bom_no, company: frm.doc.company }
            }).then(r => {
                let bom_items = r.message || [];
                bom_items.forEach(function(bi) {
                    all_rows.push({
                        custom_parent_item: pp_item.item_code,
                        item_code: bi.item_code,
                        item_name: bi.item_name || "",
                        from_warehouse: bi.default_warehouse || DEFAULT_WAREHOUSE,
                        warehouse: bi.default_warehouse || DEFAULT_WAREHOUSE,
                        material_request_type: "Manufacture",
                        quantity: bi.qty * planned_qty,
                        custom_adjusted_qty: bi.qty * planned_qty,
                        schedule_date: frm.doc.planned_date || frappe.datetime.now_date(),
                        uom: bi.uom || "",
                        description: bi.description || ""
                    });
                });
            });

            promises.push(p);
        });

        Promise.all(promises).then(() => {
            all_rows.forEach(row => {
                let r = frm.add_child("mr_items");
                Object.assign(r, row);
            });

            frm.refresh_field("mr_items");

            // Cosmetic separators
            const wrapper = $(frm.fields_dict.mr_items.grid.wrapper);
            const grouped = {};

            wrapper.find(".grid-row").each(function() {
                const row_name = $(this).attr("data-name");
                const row_doc = frappe.get_doc("Material Request Plan Item", row_name);
                if (!grouped[row_doc.custom_parent_item]) grouped[row_doc.custom_parent_item] = [];
                grouped[row_doc.custom_parent_item].push($(this));
            });

            Object.keys(grouped).forEach(function(parent_item) {
                const rows = grouped[parent_item];
                if (rows.length > 0) {
                    const first_row = rows[0];
                    const sep = $('<div>')
                        .text("--- Materials for " + parent_item + " ---")
                        .css({
                            "font-weight": "bold",
                            "background-color": "#f2f2f2",
                            "padding": "4px 8px"
                        });
                    first_row.before(sep);
                }
            });

            resolve();
        });
    });
}

// =====================
// Production Plan Client Script
// =====================

// Event on child table (Production Plan Item)
frappe.ui.form.on("Production Plan Item", {
    bom_no: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        check_bom_for_stitching(frm, row);
    }
});

// Event on main form (Production Plan)
frappe.ui.form.on("Production Plan", {
    refresh: function(frm) {
        console.log("Production Plan Client Script loaded!");

        // Re-check all BOMs on refresh so visibility stays after Save
        if (frm.doc.po_items && frm.doc.po_items.length) {
            frm.doc.po_items.forEach(row => check_bom_for_stitching(frm, row));
        } else {
            update_work_sections(frm);
        }

        // ðŸ”¹ Example filter for Work Table items (optional)
        frm.set_query("item_code", "custom_work_table", function() {
            return {
                filters: [
                    ["Item", "item_code", "in", (frm.doc.po_items || []).map(d => d.item_code)]
                ]
            };
        });
    }
});

// =====================
// Helpers
// =====================

// Call backend to check if BOM has stitching
function check_bom_for_stitching(frm, row) {
    if (!row || !row.bom_no) {
        row.has_stitching = 0;
        update_work_sections(frm);
        return;
    }

    frappe.call({
        method: "my_custom_app.api.get_bom.get_bom_operations",
        args: { bom_no: row.bom_no },
        callback: function(res) {
            if (!res.message) {
                row.has_stitching = 0;
            } else {
                let hasStitching = res.message.some(op => op.operation === "Stitching");
                row.has_stitching = hasStitching ? 1 : 0;
            }
            update_work_sections(frm); // recalc for all rows
        },
        error: function(err) {
            console.error("Error fetching BOM operations:", err);
            row.has_stitching = 0;
            update_work_sections(frm);
        }
    });
}

// Toggle UI based on ALL rows
function update_work_sections(frm) {
    let hasAnyStitching = (frm.doc.po_items || []).some(r => r.has_stitching);
    frm.toggle_display("custom_work_entries", hasAnyStitching);
    frm.toggle_display("custom_work_table", hasAnyStitching);
    console.log("Work Sections visible?", hasAnyStitching);
    let stitchingRows = (frm.doc.po_items || []).filter(d => d.has_stitching);

    if (!stitchingRows.length) {
        frm.set_df_property("custom_work_entries", "hidden", 1);
        return;
    }

    frm.set_df_property("custom_work_entries", "hidden", 0);
}

frappe.ui.form.on("Stitching Entry", {
    quantity: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        row.amount = (row.quantity || 0) * (row.rate || 0);
        frm.refresh_field("stitching_entries");
    },
    rate: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        row.amount = (row.quantity || 0) * (row.rate || 0);
        frm.refresh_field("stitching_entries");
    }
});

frappe.ui.form.on("Production Plan", {
    refresh: function(frm) {
        if (frm.doc.po_items && frm.doc.po_items.length) {
            frm.doc.po_items.forEach(row => {
                if (row.bom_no) {
                    frappe.call({
                        method: "my_custom_app.api.get_bom.get_bom_operations",
                        args: { bom_no: row.bom_no },
                        callback: function(res) {
                            if (!res.message) {
                                row.has_stitching = 0;
                            } else {
                                let hasStitching = res.message.some(op => op.operation === "Stitching");
                                row.has_stitching = hasStitching ? 1 : 0;
                            }
                            update_work_sections(frm);
                        }
                    });
                }
            });
        } else {
            update_work_sections(frm, false);
        }

        // ðŸ”¹ Filter items inside Work Table
        frm.set_query("item_code", "custom_work_table", function() {
            return {
                filters: [
                    ["Item", "item_code", "in",
                        (frm.doc.po_items || [])
                            .filter(d => d.has_stitching)   // âœ… Only stitching BOMs
                            .map(d => d.item_code)
                    ]
                ]
            };
        });
    }
});

frappe.ui.form.on("Production Plan", {
  validate(frm) {
    const toNum = v => {
      const n = parseFloat(v);
      return Number.isFinite(n) ? n : 0;
    };
    const eq = (a, b) => Math.abs(toNum(a) - toNum(b)) < 1e-9;

    const pick = (row, keys) => {
      for (const k of keys) {
        if (row && row[k] !== undefined && row[k] !== null && row[k] !== "") return row[k];
      }
      return undefined;
    };
    const add = (map, key, val) => (map[key] = toNum((map[key] || 0) + toNum(val || 0)));

    // -------------------------
    // Planned rows (filter only BOMs with stitching)
    // -------------------------
    const plannedByItem = {};
    const plannedByItemBom = {};
    const itemToBoms = {};
    (frm.doc.po_items || [])
      .filter(r => r.has_stitching)   // âœ… skip BOMs without stitching
      .forEach(r => {
        const item = r.item_code;
        const bom  = r.bom_no;
        const qty  = toNum(r.planned_qty);
        if (!item || !qty) return;
        add(plannedByItem, item, qty);
        if (bom) {
          add(plannedByItemBom, `${item}::${bom}`, qty);
          (itemToBoms[item] ||= new Set()).add(bom);
        }
      });

    // -------------------------
    // Work entries (child table)
    // -------------------------
    const workByItem = {};
    const workByItemBom = {};
    let anyWorkHasBom = false;
    (frm.doc.custom_work_table || []).forEach(r => {
      const item = pick(r, ["item", "item_code"]);
      const bom  = pick(r, ["bom", "bom_no"]);
      const qty  = toNum(pick(r, ["quantity_pcs", "quantitypcs", "qty", "quantity", "qty_pcs"]));
      if (!item || !qty) return;

      // âœ… check if the BOM on this row is stitching-enabled
      let bomHasStitching = true;
      if (bom) {
        const plannedRow = (frm.doc.po_items || []).find(p => p.item_code === item && p.bom_no === bom);
        bomHasStitching = plannedRow ? !!plannedRow.has_stitching : true;
      }
      if (!bomHasStitching) return; // skip validation for non-stitching BOM rows

      add(workByItem, item, qty);
      if (bom) {
        anyWorkHasBom = true;
        add(workByItemBom, `${item}::${bom}`, qty);
      }
    });

    // -------------------------
    // Validation checks
    // -------------------------
    const errors = [];
    if (anyWorkHasBom) {
      Object.keys(plannedByItemBom).forEach(key => {
        const planned = plannedByItemBom[key];
        const worked  = toNum(workByItemBom[key] || 0);
        if (!eq(planned, worked)) {
          const [item, bom] = key.split("::");
          errors.push(`For Item ${item} (BOM ${bom}), Work Entries total (${worked}) does not match Planned (${planned}).`);
        }
      });
      Object.keys(workByItemBom).forEach(key => {
        if (!plannedByItemBom[key]) {
          const [item, bom] = key.split("::");
          errors.push(`Work Entry exists for Item ${item} (BOM ${bom}) but it is not present in Planned items.`);
        }
      });
    } else {
      Object.entries(itemToBoms).forEach(([item, set]) => {
        if (set.size > 1) {
          errors.push(`Item ${item} has multiple BOMs planned (${Array.from(set).join(", ")}). Add/select a BOM column in Work Entries and fill it per row.`);
        }
      });
      Object.keys(plannedByItem).forEach(item => {
        const planned = plannedByItem[item];
        const worked  = toNum(workByItem[item] || 0);
        if (!eq(planned, worked)) {
          errors.push(`For Item ${item}, Work Entries total (${worked}) does not match Planned (${planned}).`);
        }
      });
      Object.keys(workByItem).forEach(item => {
        if (!plannedByItem[item]) {
          errors.push(`Work Entry exists for Item ${item} but it is not present in Planned items.`);
        }
      });
    }

    if (errors.length) {
      const esc = s => String(s).replace(/[&<>"']/g, c => ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[c]));
      const listHtml = `<ul>${errors.map(e => `<li>${esc(e)}</li>`).join("")}</ul>`;
      frappe.throw({
        title: "Validation Error",
        message: listHtml,
        indicator: "red",
      });
    }
  }
});

frappe.ui.form.on('Delivery Note', {
    validate: async function(frm) {
        // Build promises for all items
        const checks = frm.doc.items.map(async (row) => {
            if (!row.item_code || !row.warehouse) return;
            const res = await frappe.db.get_value('Bin',
                { item_code: row.item_code, warehouse: row.warehouse },
                'actual_qty');

            const available = (res && res.message && res.message.actual_qty) ? flt(res.message.actual_qty, 6) : 0;
            if (available < flt(row.qty, 6)) {
                row.qty = available;
                row._auto_adjusted = true; // marker for UI message
            }
        });

        // Wait for all DB calls to finish
        await Promise.all(checks);

        // If any changes were made, refresh the child table so user sees them
        if (frm.doc.items.some(i => i._auto_adjusted)) {
            frm.refresh_field('items');
            frappe.msgprint(__('Some item quantities were reduced to available stock (partial delivery).'));
            // optional: remove markers
            frm.doc.items.forEach(i => { delete i._auto_adjusted; });
        }
    }
});

frappe.ui.form.on("Stock Entry", {
    onload: function(frm) {
        frm.set_query("stock_entry_type", function() {
            return {
                filters: [
                    ["name", "in", ["Material Transfer", "Material Receipt"]]
                ]
            };
        });
    }
});

frappe.ui.form.on("Stock Entry", {
    stock_entry_type: function(frm) {
        toggle_source_warehouse(frm);
    },
    onload: function(frm) {
        toggle_source_warehouse(frm);
    }
});

frappe.ui.form.on("Stock Entry", {
    stock_entry_type: function(frm) {
        toggle_source_warehouse(frm);
    },
    onload: function(frm) {
        toggle_source_warehouse(frm);
    }
});

function toggle_source_warehouse(frm) {
    if (frm.doc.stock_entry_type === "Material Receipt") {
        frm.fields_dict["items"].grid.update_docfield_property("s_warehouse", "hidden", 1);

        // Optional: also clear existing values
        frm.doc.items.forEach(d => d.s_warehouse = "");
        frm.refresh_field("items");

    } else {
        frm.fields_dict["items"].grid.update_docfield_property("s_warehouse", "hidden", 0);
    }
}
