 "script": "frappe.ui.form.on("Production Plan Item", {
    bom_no: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.bom_no) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "BOM",
                    name: row.bom_no
                },
                callback: function(r) {
                    if (r.message) {
                        frm.clear_table("custom_boms");
                        r.message.items.forEach(function(i) {
                            let child = frm.add_child("custom_boms");
                            child.item_code = i.item_code;
                            child.item_name = i.item_name;
                            child.uom = i.uom;
                            child.qty = i.qty * row.planned_qty; // adjusts with planned qty
                        });
                        frm.refresh_field("custom_boms");
                    }
                }
            });
        }
    }
});
",
 "view": "Form"
}

