 "script": "frappe.ui.form.on('Production Plan', {
    refresh: function(frm) {
        // Add our custom button to the form
        frm.add_custom_button(__('Get Segregated Raw Materials'), function() {
            frappe.show_alert({ message: __('Fetching Segregated Materials...'), indicator: 'blue' });

            // Clear any existing items in the raw materials table
            frm.clear_table('mr_items');

            // Call the Server Script API we created in Step 2
            frappe.call({
                // This is the API Method name from the Server Script
                method: 'get_segregated_materials', 
                args: {
                    // We send the name of the current production plan to our script
                    production_plan_doc: frm.doc.name
                },
                callback: function(r) {
                    // This part is the same as before
                    if (r.message && r.message.length > 0) {
                        r.message.forEach(function(item) {
                            var child = frm.add_child('mr_items', {
                                item_code: item.item_code,
                                qty_as_per_bom: item.qty_as_per_bom,
                                parent_assembly_item: item.parent_assembly_item
                            });
                        });
                        frm.refresh_field('mr_items');
                        frappe.show_alert({ message: __('Segregated materials have been loaded.'), indicator: 'green' });
                    } else {
                         frappe.show_alert({ message: __('No raw materials found or an error occurred.'), indicator: 'orange' });
                    }
                }
            });
        }).addClass('btn-primary');
    }
});",
 "view": "Form"
}

