 "script": "frappe.ui.form.on('Multi Report View', {
  refresh: function(frm) {
        // Create container for two tables
        frm.fields_dict.report_html.$wrapper.html(`
            <div style="display:flex; gap:20px; flex-wrap: wrap;">
    <div style="flex:1; min-width:400px;">
        <h4>Stitching Work Today</h4>
        <iframe src="/app/query-report/Stitching%20Work%20Today?standard_filter=1" 
                style="width:100%; height:600px; border:1px solid #ccc;"></iframe>
    </div>
    <div style="flex:1; min-width:400px;">
        <h4>Cutting Section</h4>
        <iframe src="/app/query-report/Cutting%20Section?standard_filter=1" 
                style="width:100%; height:600px; border:1px solid #ccc;"></iframe>
    </div>
</div>

        `);

        // Function to render a Query Report table
        const render_report = (report_name, selector) => {
            frappe.call({
                method: "frappe.desk.query_report.run",
                args: {
                    report_name: report_name,
                    filters: {}  // you can pass filter values here
                },
                callback: function(r) {
                    if(r.message && r.message.result) {
                        let columns = r.message.columns.map(c => `<th>${c.label}</th>`).join('');
                        let rows = r.message.result.map(row => 
                            `<tr>${row.map(cell => `<td>${cell}</td>`).join('')}</tr>`).join('');
                        $(`${selector} .table-wrapper`).html(`
                            <table class="table table-bordered table-striped">
                                <thead><tr>${columns}</tr></thead>
                                <tbody>${rows}</tbody>
                            </table>
                        `);
                    }
                }
            });
        };

        // Render both reports
        render_report("Stitching Work Today", "#stitching_report");
        render_report("Cutting Section", "#cutting_report");
    }
});",
 "view": "Form"
}
